def expand_universe(input_list: list[str], expend_by) -> list[str]:
    column_count = len(input_list[0])

    # Find column and row indexes that only contains "."
    col_indexes = [
        i for i in range(column_count) if all(row[i] == "." for row in input_list)
    ]
    row_indexes = [
        i for i, row in enumerate(input_list) if all(char == "." for char in row)
    ]
    actual_row_indexes = [i for i in range(len(input_list))]
    row_index_copy = actual_row_indexes.copy()
    current_offset = 0

    for index, num in enumerate(row_index_copy):
        actual_row_indexes[index] += current_offset
        if num in row_indexes:
            current_offset += expend_by

    actual_col_indexes = [i for i in range(column_count)]
    col_index_copy = actual_col_indexes.copy()
    current_offset = 0

    for index, num in enumerate(col_index_copy):
        actual_col_indexes[index] += current_offset
        if num in col_indexes:
            current_offset += expend_by

    return actual_row_indexes, actual_col_indexes


def get_positions(input, actual_row_indexes, actual_col_indexes):
    positions = {}
    count = 1
    for row_index, row in enumerate(input):
        for col_index, cell in enumerate(row):
            if cell == "#":
                positions[count] = (
                    actual_row_indexes[row_index],
                    actual_col_indexes[col_index],
                )
                count += 1

    return positions


def solution(points):
    total_sum = 0
    for i, j in itertools.combinations(points, 2):
        total_sum += abs(points[i][0] - points[j][0]) + abs(points[i][1] - points[j][1])

    return total_sum


if __name__ == "__main__":
    input = read_input("inputs/day11.txt")
    actual_row_indexes, actual_col_indexes = expand_universe(input, 1)
    positions = get_positions(input, actual_row_indexes, actual_col_indexes)
    print(f"Solution 1: {solution(positions)}")
    actual_row_indexes, actual_col_indexes = expand_universe(input, 999999)
    positions = get_positions(input, actual_row_indexes, actual_col_indexes)

    print(f"Solution 2: {solution(positions)}")
